import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import Projects from "./Projects";
import Blog from "./Blog";
import SocialMedia from "./SocialMedia";
import Services from "./Services";
import About from "./About";
import Homepage from "./Homepage";

// Inline styles for quick integration
const styles = {
  app: {
    display: "flex",
    height: "100vh",
    flexDirection: "column",
  },
  header: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "10px 20px",
    backgroundColor: "#ffffff",
    borderBottom: "1px solid #070505",
  },
  logo: {
    height: "150px",
    width: "auto",
    marginRight: "2px",
  },
  navLinks: {
    listStyle: "none",
    display: "flex",
    margin: 0,
    padding: 0,
  },
  navItem: {
    marginLeft: "20px",
  },
  navLink: {
    textDecoration: "none",
    color: "#333",
    fontSize: "16px",
  },
  mainContent: {
    flex: 1,
    padding: "20px",
    overflow: "auto",
  },
  // Styles for dropdown menu and button (for small screens)
  dropdownButton: {
    color: "#333",
    fontSize: "24px",
    padding: "10px",
    cursor: "pointer",
    border: "none",
    backgroundColor: "transparent",  // Transparent background
    zIndex: 2,
  },
  dropdownMenu: {
    listStyle: "none",
    backgroundColor: "black",
    padding: "10px 0",
    margin: 0,
    display: "none",  // Hidden by default
    flexDirection: "column",
    position: "absolute",
    top: "60px",  // Adjust the positioning as needed
    right: "20px",  // Adjust the positioning as needed
    width: "200px", // Set a fixed width for the dropdown
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
  },
  dropdownMenuOpen: {
    display: "flex",  // Show menu when open
  },
  burgerBox: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    width: "30px",
    height: "20px",
  },
  burgerLine: {
    height: "4px",
    width: "100%",
    backgroundColor: "#333",  // Dark color for the lines
    borderRadius: "2px",
  },
};

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Track window size for mobile responsiveness
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    // Initial check
    handleResize();

    // Event listener for window resize
    window.addEventListener("resize", handleResize);

    // Cleanup listener on component unmount
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Toggle the dropdown menu
  const toggleNav = () => setIsOpen(!isOpen);

  return (
    <Router>
      <div style={styles.app}>
        <header style={styles.header}>
          <div>
            <Link to="/">
              <img
                src="/starlightlogo.png"
                alt="Starlight Technology Logo"
                style={styles.logo}
              />
            </Link>
          </div>
          <nav>
            {/* Dropdown button for small screens */}
            {isMobile && (
              <div
                style={styles.dropdownButton}
                onClick={toggleNav}
                aria-expanded={isOpen ? "true" : "false"} // Added aria-expanded for accessibility
                aria-label="Toggle navigation menu"
              >
                {isOpen ? "âˆ’" : "+"}
              </div>
            )}

            {/* Navigation links */}
            <ul
              style={{
                ...styles.navLinks,
                ...(isMobile ? { display: "none" } : {}),
                ...(isOpen && isMobile ? styles.dropdownMenuOpen : {}),
              }}
            >
              <li style={styles.navItem}>
                <Link to="/projects" style={styles.navLink}>
                  Projects
                </Link>
              </li>
              <li style={styles.navItem}>
                <Link to="/blog" style={styles.navLink}>
                  Blog
                </Link>
              </li>
              <li style={styles.navItem}>
                <Link to="/social-media" style={styles.navLink}>
                  Social Media
                </Link>
              </li>
              <li style={styles.navItem}>
                <Link to="/services" style={styles.navLink}>
                  Services
                </Link>
              </li>
              <li style={styles.navItem}>
                <Link to="/about" style={styles.navLink}>
                  About
                </Link>
              </li>
            </ul>
          </nav>
        </header>

        <div style={styles.mainContent}>
          <Routes>
            <Route path="/" element={<Homepage />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/social-media" element={<SocialMedia />} />
            <Route path="/services" element={<Services />} />
            <Route path="/about" element={<About />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
