import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import Projects from "./Projects";
import Blog from "./Blog";
import SocialMedia from "./SocialMedia";
import Services from "./Services";
import About from "./About";
import Homepage from "./Homepage";

// Inline styles for quick integration
const styles = {
  app: {
    display: "flex",
    height: "100vh",
    flexDirection: "column",
  },
  header: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "10px 20px",
    backgroundColor: "#ffffff",
    borderBottom: "1px solid #070505",
  },
  logo: {
    height: "150px",
    width: "auto",
    marginRight: "2px",
  },
  navLinks: {
    listStyle: "none",
    display: "flex",
    margin: 0,
    padding: 0,
  },
  navItem: {
    marginLeft: "20px",
  },
  navLink: {
    textDecoration: "none",
    color: "#333",
    fontSize: "16px",
  },
  mainContent: {
    flex: 1,
    padding: "20px",
    overflow: "auto",
  },
  // Styles for dropdown menu and button (for small screens)
  dropdownButton: {
    fontSize: "24px",
    padding: "10px",
    cursor: "pointer",
    border: "none",
    backgroundColor: "transparent",  // Transparent background
    zIndex: 2,
    color: "#333", // Default black color
  },
  dropdownButtonOpen: {
    color: "white",  // White color when the menu is open
  },
  dropdownMenu: {
    listStyle: "none",
    backgroundColor: "black",
    padding: "10px 0",
    margin: 0,
    display: "none",  // Hidden by default
    flexDirection: "column",
    position: "absolute",
    top: 0,
    right: 0,
    left: 0,
    bottom: 0,
    zIndex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  dropdownMenuOpen: {
    display: "flex",  // Show menu when open
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    justifyContent: "center",
    alignItems: "center",
  },
  burgerBox: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    width: "30px",
    height: "20px",
  },
  burgerLine: {
    height: "4px",
    width: "100%",
    backgroundColor: "#333",  // Dark color for the lines
    borderRadius: "2px",
  },
  fullScreenMenu: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    position: "fixed", // Fixed to the screen
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "black",
    zIndex: 2,
  },
  fullScreenLink: {
    color: "white",
    fontSize: "24px",
    textDecoration: "none",
    padding: "10px",
    margin: "10px 0",
  },
  headerContainer: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%", // Make sure the container takes the full width
  },
  logoContainer: {
    display: "flex",
    alignItems: "center",
  },
  menuButtonContainer: {
    display: "flex",
    justifyContent: "flex-end",  // Position the menu button on the right
    width: "100%",
  }
};

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Track window size for mobile responsiveness
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 800); // Adjusting the threshold to 800px
    };

    // Initial check
    handleResize();

    // Event listener for window resize
    window.addEventListener("resize", handleResize);

    // Cleanup listener on component unmount
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Toggle the dropdown menu
  const toggleNav = () => setIsOpen(!isOpen);

  // Reset the menu when navigating to a new page
  const resetNav = () => setIsOpen(false);

  return (
    <Router>
      <div style={styles.app}>
        <header style={styles.header}>
          <div style={styles.headerContainer}>
            {/* Logo aligned to the left */}
            <div style={styles.logoContainer}>
              <Link to="/">
                <img
                  src="/starlightlogo.png"
                  alt="Starlight Technology Logo"
                  style={styles.logo}
                />
              </Link>
            </div>

            {/* Dropdown button aligned to the right */}
            {isMobile && (
              <div style={styles.menuButtonContainer}>
                <div
                  style={{
                    ...styles.dropdownButton,
                    ...(isOpen ? styles.dropdownButtonOpen : {}),
                  }}
                  onClick={toggleNav}
                >
                  {isOpen ? "âˆ’" : "+"}
                </div>
              </div>
            )}
          </div>

          {/* Regular Navigation Links for larger screens */}
          {!isMobile && (
            <nav>
              <ul style={styles.navLinks}>
                <li style={styles.navItem}>
                  <Link to="/projects" style={styles.navLink}>
                    Projects
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/blog" style={styles.navLink}>
                    Blog
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/social-media" style={styles.navLink}>
                    Social Media
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/services" style={styles.navLink}>
                    Services
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/about" style={styles.navLink}>
                    About
                  </Link>
                </li>
              </ul>
            </nav>
          )}
        </header>

        <div style={styles.mainContent}>
          <Routes>
            <Route path="/" element={<Homepage />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/social-media" element={<SocialMedia />} />
            <Route path="/services" element={<Services />} />
            <Route path="/about" element={<About />} />
          </Routes>
        </div>

        {/* Full-Screen Dropdown Menu for small screens */}
        {isMobile && isOpen && (
          <div style={styles.fullScreenMenu}>
            <Link to="/projects" style={styles.fullScreenLink} onClick={resetNav}>
              Projects
            </Link>
            <Link to="/blog" style={styles.fullScreenLink} onClick={resetNav}>
              Blog
            </Link>
            <Link to="/social-media" style={styles.fullScreenLink} onClick={resetNav}>
              Social Media
            </Link>
            <Link to="/services" style={styles.fullScreenLink} onClick={resetNav}>
              Services
            </Link>
            <Link to="/about" style={styles.fullScreenLink} onClick={resetNav}>
              About
            </Link>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
