import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import Projects from "./Projects";
import Blog from "./Blog";
import SocialMedia from "./SocialMedia";
import Services from "./Services";
import About from "./About";
import Industries from "./Industries"; // Assuming you have the Industries component
import Homepage from "./Homepage";

// Inline styles for quick integration
const styles = {
  app: {
    display: "flex",
    height: "100vh",
    flexDirection: "column",
  },
  header: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "10px 20px",
    backgroundColor: "#ffffff",
    borderBottom: "1px solid #070505",
  },
  logo: {
    height: "150px",
    width: "auto",
    marginRight: "20px",
  },
  navLinks: {
    listStyle: "none",
    display: "flex", // Ensures horizontal layout
    margin: 0,
    padding: 0,
    justifyContent: "space-between", // Distribute space evenly
    flex: 1, // Make the navbar items fill the space
  },
  navItem: {
    marginLeft: "40px", // Adjust the space between navbar items
  },
  navLink: {
    textDecoration: "none",
    color: "#333",
    fontSize: "29px", // Increase font size
    fontWeight: "bold", // Make the font bold
  },
  mainContent: {
    flex: 1,
    padding: "20px",
    overflow: "auto",
  },
  dropdownButton: {
    fontSize: "24px",
    padding: "10px",
    cursor: "pointer",
    border: "none",
    backgroundColor: "transparent",
    zIndex: 2,
    color: "#333",
    transition: "color 0.3s",
  },
  dropdownButtonOpen: {
    color: "white",
  },
  dropdownMenu: {
    listStyle: "none",
    backgroundColor: "black",
    padding: "10px 0",
    margin: 0,
    display: "none",
    flexDirection: "column",
    position: "absolute",
    top: 0,
    right: 0,
    left: 0,
    bottom: 0,
    zIndex: 1,
    justifyContent: "center",
    alignItems: "center",
    transition: "all 0.3s ease-in-out",
  },
  dropdownMenuOpen: {
    display: "flex",
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    justifyContent: "center",
    alignItems: "center",
  },
  burgerBox: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    width: "30px",
    height: "30px",
    zIndex: 3,
    cursor: "pointer",
    position: "absolute",
    right: "20px",
    "&:hover": {
      opacity: 0.8,
    },
  },
  burgerLine: {
    height: "4px",
    width: "100%",
    backgroundColor: "#333",
    borderRadius: "2px",
    transition: "all 0.3s",
  },
  burgerLineOpen1: {
    transform: "rotate(45deg) translateY(7px)",
    backgroundColor: "white",
  },
  burgerLineOpen2: {
    opacity: 0,
  },
  burgerLineOpen3: {
    transform: "rotate(-45deg) translateY(-7px)",
    backgroundColor: "white",
  },
  fullScreenMenu: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "black",
    zIndex: 2,
    transition: "all 0.3s ease-in-out",
  },
  fullScreenLink: {
    color: "white",
    fontSize: "24px",
    textDecoration: "none",
    padding: "10px",
    margin: "10px 0",
  },
  headerContainer: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%",
  },
  logoContainer: {
    display: "flex",
    alignItems: "center",
  },
  menuButtonContainer: {
    display: "flex",
    justifyContent: "flex-end",
    width: "100%",
  },
};

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 800); // Adjusting the threshold to 800px
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const toggleNav = () => setIsOpen(!isOpen);
  const resetNav = () => setIsOpen(false);
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
  }, [isOpen]);

  return (
    <Router>
      <div style={styles.app}>
        <header style={styles.header}>
          <div style={styles.headerContainer}>
            {/* Logo aligned to the left */}
            <div style={styles.logoContainer}>
              <Link to="/">
                <img
                  src="/starlightlogo.png"
                  alt="Starlight Technology Logo"
                  style={styles.logo}
                />
              </Link>
            </div>

            {/* Burger icon turned into "X" when clicked */}
            {isMobile && (
              <div style={styles.menuButtonContainer}>
                <div style={styles.burgerBox} onClick={toggleNav}>
                  <div
                    style={{
                      ...styles.burgerLine,
                      ...(isOpen ? styles.burgerLineOpen1 : {}),
                    }}
                  />
                  <div
                    style={{
                      ...styles.burgerLine,
                      ...(isOpen ? styles.burgerLineOpen2 : {}),
                    }}
                  />
                  <div
                    style={{
                      ...styles.burgerLine,
                      ...(isOpen ? styles.burgerLineOpen3 : {}),
                    }}
                  />
                </div>
              </div>
            )}
          </div>

          {/* Regular Navigation Links for larger screens */}
          {!isMobile && (
            <nav>
              <ul style={styles.navLinks}>
                <li style={styles.navItem}>
                  <Link to="/projects" style={styles.navLink}>
                    Why Us
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/services" style={styles.navLink}>
                    Services
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/industries" style={styles.navLink}>
                    Industries
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/social-media" style={styles.navLink}>
                    SLT Insider
                  </Link>
                </li>
                <li style={styles.navItem}>
                  <Link to="/about" style={styles.navLink}>
                    Contact
                  </Link>
                </li>
              </ul>
            </nav>
          )}
        </header>

        <div style={styles.mainContent}>
          <Routes>
            <Route path="/" element={<Homepage />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/services" element={<Services />} />
            <Route path="/industries" element={<Industries />} /> {/* New route for Industries */}
            <Route path="/social-media" element={<SocialMedia />} />
            <Route path="/about" element={<About />} />
          </Routes>
        </div>

        {/* Full-Screen Dropdown Menu for small screens */}
        {isMobile && isOpen && (
          <div style={styles.fullScreenMenu}>
            <Link to="/projects" style={styles.fullScreenLink} onClick={resetNav}>
              Why Us
            </Link>
            <Link to="/services" style={styles.fullScreenLink} onClick={resetNav}>
              Services
            </Link>
            <Link to="/industries" style={styles.fullScreenLink} onClick={resetNav}>
              Industries
            </Link>
            <Link to="/social-media" style={styles.fullScreenLink} onClick={resetNav}>
              SLT Insider
            </Link>
            <Link to="/about" style={styles.fullScreenLink} onClick={resetNav}>
              Contact
            </Link>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
