import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const styles = {
  topNavBar: {
    backgroundColor: '#003366', // Dark blue background
    color: 'white',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-between', // Space out the elements
    alignItems: 'center',
    fontSize: '14px', // Smaller font
    flexWrap: 'wrap', // Ensure items wrap on small screens
  },
  topNavItem: {
    display: 'flex',
    alignItems: 'center',
    margin: '5px', // Add space between items
  },
  phoneButton: {
    marginLeft: '10px',
    padding: '5px 10px',
    backgroundColor: '#ffffff',
    color: '#003366',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  scheduleButton: {
    backgroundColor: '#add8e6', // Light blue
    color: '#003366',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  hiddenMobile: {
    display: 'none',
  },
};

function TopNavBar() {
  const [isMobile, setIsMobile] = useState(false);

  // Update the screen size state on resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 800);
    };
    handleResize(); // Run once to check on initial load
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div style={styles.topNavBar}>
      {/* Contact Support Section */}
      <div style={styles.topNavItem}>
        <a href="tel:0000000000" style={styles.phoneButton}>Support</a>
      </div>

      {/* IT Support Section, always visible */}
      <div style={styles.topNavItem}>
        <a href="tel:000000000" style={styles.phoneButton}>IT Support</a>
      </div>

      {/* Schedule Consultation Button */}
      <div style={isMobile ? {} : styles.hiddenMobile}>
        <Link to="/consultation" style={styles.scheduleButton}>Schedule Consultation</Link>
      </div>

      {/* For Mobile, Show Support and Consultation Button */}
      {isMobile && (
        <div style={styles.topNavItem}>
          <span>Contact Support: 000-000-0000</span>
          <a href="tel:0000000000" style={styles.phoneButton}>Call</a>
        </div>
      )}
    </div>
  );
}

export default TopNavBar;
