import React from "react";
import { Link } from "react-router-dom";

const styles = {
  section: {
    padding: "20px",
    backgroundColor: "#f2f2f2",
    borderRadius: "10px",
    marginBottom: "20px",
  },
  review: {
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    marginBottom: "20px",
  },
  stars: {
    color: "#FFD700", // Gold color for the stars
    fontSize: "18px",
    marginBottom: "5px",
  },
  reviewText: {
    marginBottom: "10px",
    fontSize: "16px",
  },
  reviewer: {
    fontStyle: "italic",
    fontSize: "14px",
  },
  writeReviewButton: {
    backgroundColor: "#333",
    color: "#fff",
    padding: "10px 20px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    fontSize: "16px",
    transition: "transform 0.3s",
  },
  writeReviewButtonHover: {
    transform: "scale(1.1)",
  },
};

function Homepage() {
  return (
    <div>
      <h1>Welcome to Starlight Technology!</h1>
      
      <div style={styles.section}>
        <h2>Customer Reviews</h2>
        
        {/* Review 1 */}
        <div style={styles.review}>
          <div style={styles.stars}>★★★★★</div>
          <div style={styles.reviewText}>
            "The team at Starlight Technology transformed our business processes. 
            Their insights and technology solutions are unparalleled!"
          </div>
          <div style={styles.reviewer}>- Alex Johnson</div>
        </div>
        
        {/* Review 2 */}
        <div style={styles.review}>
          <div style={styles.stars}>★★★★★</div>
          <div style={styles.reviewText}>
            "Amazing service! They delivered exactly what we needed on time and 
            exceeded our expectations."
          </div>
          <div style={styles.reviewer}>- Jamie Lee</div>
        </div>
        
        {/* Review 3 */}
        <div style={styles.review}>
          <div style={styles.stars}>★★★★★</div>
          <div style={styles.reviewText}>
            "Working with Starlight Technology has been a game-changer for our 
            business. Highly recommend them!"
          </div>
          <div style={styles.reviewer}>- Taylor Smith</div>
        </div>
        
        {/* Write a Review Button */}
        <div style={{ textAlign: "right" }}>
          <Link to="/review">
            <button style={styles.writeReviewButton}>
              Write a Review
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Homepage;
