import React from "react";
import { useNavigate } from "react-router-dom";

const styles = {
  missionSection: {
    backgroundColor: "#007BFF", // Blue background
    color: "#D3D3D3", // Light grey font
    fontFamily: "Times New Roman, serif",
    textAlign: "center",
    padding: "60px 20px",
    fontSize: "24px",
    fontWeight: "bold",
    lineHeight: "1.5",
  },
  reviewsSection: {
    padding: "40px 20px",
    backgroundColor: "#343A40", // Light grey background for contrast
    textAlign: "center",
    position: "relative",
  },
  reviewCard: {
    display: "inline-block",
    width: "30%",
    margin: "10px",
    padding: "20px",
    backgroundColor: "#FFFFFF",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    fontSize: "16px",
    lineHeight: "1.4",
  },
  writeReviewButton: {
    position: "absolute",
    bottom: "20px",
    right: "20px",
    padding: "10px 20px",
    backgroundColor: "#007BFF",
    color: "#FFFFFF",
    border: "none",
    borderRadius: "5px",
    fontSize: "16px",
    cursor: "pointer",
    transition: "all 0.3s ease",
  },
  writeReviewButtonHover: {
    backgroundColor: "#0056b3",
    fontSize: "18px",
    padding: "12px 24px",
  },
  footer: {
    backgroundColor: "#343A40",
    color: "#D3D3D3",
    textAlign: "center",
    padding: "20px",
    fontSize: "14px",
  },
  footerLink: {
    color: "#FFFFFF",
    textDecoration: "none",
    margin: "0 10px",
  },
};

function Homepage() {
  const navigate = useNavigate();

  const handleWriteReviewClick = () => {
    navigate("/review"); // Assuming the route for Review.js is "/review"
  };

  return (
    <div>
      {/* Mission Statement Section */}
      <div style={styles.missionSection}>
        Empowering businesses through innovative web and data solutions, transforming ideas into scalable digital realities. 
        We deliver expert consulting and seamless implementation to drive growth, efficiency, and long-term success.
      </div>

      {/* Reviews Section */}
      <div style={styles.reviewsSection}>
        <div style={styles.reviewCard}>
          <strong>Company A:</strong>
          <p>"Starlight Technology revolutionized our data systems. Their expertise is unparalleled!"</p>
        </div>
        <div style={styles.reviewCard}>
          <strong>Company B:</strong>
          <p>"We saw measurable growth thanks to their web development services. Highly recommend!"</p>
        </div>
        <div style={styles.reviewCard}>
          <strong>Company C:</strong>
          <p>"Their consultation sessions were insightful and transformative for our business."</p>
        </div>

        {/* Write a Review Button */}
        <button
          style={styles.writeReviewButton}
          onMouseEnter={(e) =>
            Object.assign(e.target.style, styles.writeReviewButtonHover)
          }
          onMouseLeave={(e) =>
            Object.assign(e.target.style, styles.writeReviewButton)
          }
          onClick={handleWriteReviewClick}
        >
          Write a Review
        </button>
      </div>

      {/* Footer */}
      <footer style={styles.footer}>
        <div>
          &copy; {new Date().getFullYear()} Starlight Technology. 
          <a href="/careers" style={styles.footerLink}>
            Careers
          </a>
          |
          <a href="/help" style={styles.footerLink}>
            Help
          </a>
        </div>
      </footer>
    </div>
  );
}

export default Homepage;
